/* * Copyright (C) 2017 Miles Talmey. * Distributed under the MIT License (license terms are at http://opensource.org/licenses/MIT). */package com.emarte.regurgitator.test;import com.emarte.regurgitator.core.CreateResponseXmlLoader;import com.emarte.regurgitator.core.RegurgitatorException;import org.junit.Test;import static com.emarte.regurgitator.core.ConfigurationFile.loadFile;import static com.emarte.regurgitator.test.CoreLoaderTestExpectations.*;public class CreateResponseXmlLoaderTest extends XmlLoaderTest {    public CreateResponseXmlLoaderTest() {        super(new CreateResponseXmlLoader());    }    @Test    public void testValue() throws Exception {        assertExpectation("classpath:/CreateResponse_value.xml", CreateResponse_value);    }    @Test    public void testFile() throws Exception {        assertExpectation("classpath:/CreateResponse_file.xml", CreateResponse_file);    }    @Test    public void testSource() throws Exception {        assertExpectation("classpath:/CreateResponse_source.xml", CreateResponse_source);    }    @Test    public void testSourceAndValue() throws Exception {        assertExpectation("classpath:/CreateResponse_sourceAndValue.xml", CreateResponse_sourceAndValue);    }    @Test    public void testSourceAndFile() throws Exception {        assertExpectation("classpath:/CreateResponse_sourceAndFile.xml", CreateResponse_sourceAndFile);    }    @Test    public void testProcessor() throws Exception {        assertExpectation("classpath:/CreateResponse_processor.xml", CreateResponse_processor);    }    @Test    public void testProcessorFlat() throws Exception {        assertExpectation("classpath:/CreateResponse_processorFlat.xml", CreateResponse_processorFlat);    }    @Test    public void testMultipleProcessors() throws Exception {        assertExpectation("classpath:/CreateResponse_multipleProcessors.xml", CreateResponse_multipleProcessors);    }    @Test    public void testMultipleProcessorsFlat() throws Exception {        assertExpectation("classpath:/CreateResponse_multipleProcessorsFlat.xml", CreateResponse_multipleProcessorsFlat);    }    @Test    public void testFullLoad() throws Exception {        loadFile("classpath:/CreateResponse_file.xml");    }    @Test(expected = RegurgitatorException.class)    public void testValueAndFileException() throws Exception {        loadFromFile("classpath:/CreateResponse_valueAndFile.xml");    }    @Test(expected = RegurgitatorException.class)    public void testNothingException() throws Exception {        loadFromFile("classpath:/CreateResponse_nothing.xml");    }    @Test(expected = RegurgitatorException.class)    public void testMissingFileException() throws Exception {        loadFromFile("classpath:/CreateResponse_missingFile.xml");    }}